import React, { useEffect } from "react"
import AccountBalanceWalletTwoToneIcon from '@mui/icons-material/AccountBalanceWalletTwoTone'
import { Button } from "@material-tailwind/react"
import WalletNetwork from "./WalletNetwork"
import { useWeb3React } from "@web3-react/core"
import { connectors } from "../../helpers/connectors"
window.Buffer = window.Buffer || require("buffer").Buffer
function Modal({ children, shown, close }: any) {
  return shown ? (
    <div className="modal-backdrop" onClick={() => { close() }}>
      <div className="modal-content" onClick={e => { e.stopPropagation() }}>
        {children}
      </div>
    </div>
  ) : null;
}
const WalletModal = () => {
  const {
    library,
    account,
    activate,
    active
  } = useWeb3React();
  const [modalShown, toggleModal] = React.useState(false);
  useEffect(()=>{
    console.log(account)
  },[active, account, library])
  return (
    <>
      <Button onClick={() => {
        toggleModal(!modalShown);
      }} className="black bg-orange-400 rounded-lg text-md py-3 px-5 h-12 mt-3 hover:bg-orange-500"><AccountBalanceWalletTwoToneIcon />
        {account === null ? 'Connect Wallet' : account ? `${account.substring(0, 6)}...${account.substring(account.length - 4)}` : 'Connect Wallet'}</Button>
      <Modal shown={modalShown} close={() => { toggleModal(false) }}>
        <h1 className="px-3 text-center modal-title">1. Choose Network</h1>
        <div className="flex items-center flex-row flex-wrap">
          <WalletNetwork icon="data:image/png;base64," name="Ethereum" />
          <WalletNetwork icon="data:image/png;base64," name="Binance Smart Chain" />
          <WalletNetwork name="Polygon (Matic)" />
          <WalletNetwork name="Rinkeby Testnet" />
        </div>
        <h1 className="px-3 text-center modal-title">2. Choose Wallet</h1>
        <div className="flex items-center flex-row flex-wrap">
          <div className="network_wrapper hover:bg-blue-300"
            onClick={() => { activate(connectors.injected);}}>
            <img alt="Metamask" src="data:image/svg+xml;base64," />
            <span>Metamask</span>
            <p>Connect to your MetaMask</p>
          </div>
          <div className="network_wrapper hover:bg-blue-300" onClick={() => { activate(connectors.coinbaseWallet); console.log(library) }}>
            <img alt="Coinbase" src="data:image/png;base64," />
            <span>Coinbase Wallet</span>
          </div>
          <div className="network_wrapper hover:bg-blue-300" onClick={() => {
            activate(connectors.bsc);
            console.log(library)
          }}>
            <img alt="Binance" src="data:image/png;base64," />
            <span>Binance Chain Wallet</span>
          </div>
          <div className="network_wrapper hover:bg-blue-300" onClick={() => {
            activate(connectors.walletConnect);
          }}>
            <img alt="Wallet Connect" src="data:image/svg+xml;base64," />
            <span>Wallet Connect</span>
          </div>
        </div>
      </Modal>
    </>
  );
}
export default WalletModal;


